import React, { Component } from 'react';

class Counter extends Component {
    state = {
        count: 0,
        imageUrl: 'https://www.google.com/imgres?imgurl=https%3A%2F%2Fimages.theconversation.com%2Ffiles%2F93616%2Foriginal%2Fimage-20150902-6700-t2axrz.jpg%3Fixlib%3Drb-1.1.0%26q%3D45%26auto%3Dformat%26w%3D1000%26fit%3Dclip&imgrefurl=https%3A%2F%2Ftheconversation.com%2Fyes-google-has-a-new-logo-but-why-46976&tbnid=y1O2nO-LvLIv9M&vet=12ahUKEwiSn-f3kcjuAhV5HbcAHTeCDREQMygEegUIARDXAQ..i&docid=NXYFd0qpxxO8uM&w=1000&h=1000&q=google%20image&ved=2ahUKEwiSn-f3kcjuAhV5HbcAHTeCDREQMygEegUIARDXAQ'
    };

    render(){
        return ( 
        <React.Fragment>
            <img src={this.state.imageUrl} alt=''/>
            <span>{this.formatCount()}</span>
            <button>Increment</button>
        </React.Fragment>
        );
    }

    formatCount() {
        return this.state.count === 0 ? <h1>Zero</h1> : this.state.count;
    }
}

export default Counter;
