import React, { Component } from 'react';

class Counter extends Component {
    state = {
        value: this.props.counter.value,
    };

    // constructor() {
    //     super();
    //     this.handleIncrement = this.handleIncrement.bind(this);
    // }



    handleIncrement = (product) => {
        console.log(product);
        this.setState({ value: this.state.value + 1 });
    }


    handleDecrement = () => {
        this.setState({ value: (this.state.value === 0) ? 0 : this.state.value - 1 })
    }

    render(){
        return ( 
        <div>
            <h4>Counter #{this.props.counter.id}</h4>
            <button onClick={this.handleDecrement} className='btn btn-secondary btn-sm'>Decrement</button>
            <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
            <button onClick={() => this.handleIncrement({ id:this.props.counter.id})} className='btn btn-secondary btn-sm'>Increment</button>
            <button onClick={() => this.props.onDelete(this.props.counter.id)} className= 'btn btn-danger btn-sm m-2'>DELETE</button>
        </div>
        );
    }

    getBadgeClasses = () => {
        let classes = "badge m-2 badge-";
        classes += (this.state.value === 0) ? "warning" : "primary";
        return classes;
    }

    formatCount = () => {

        return this.state.value === 0 ? 'Zero' : this.state.value;
    }
}

export default Counter;
